import axios from "axios";
import React, { useEffect, useState } from "react";
import avatar from "../../../../../component/Layout/assets/avatar-user.png";

export default function FriendListItem({ friend }) {
  const [base64, setBase64] = useState();
  const getImageFromUrl = async (url, header) => {
    let result = await axios.get(url, header);
    return result;
  };

  useEffect(() => {}, []);

  return (
    <>
      <li className="FriendListItem">
        <img
          className="UserListItemAvatar"
          src={friend.picture || avatar}
          alt={`avatar of ${friend.username}`}
        />
        <div className="UserListItemName">{friend.username}</div>
      </li>
    </>
  );
}
