import React from "react";
import defaultAvatar from "../../../../component/Layout/assets/avatar-user.png";
import PropTypes from "prop-types";

ChatBoxMessage.prototype = {
  messageInfo: PropTypes.object,
  message: PropTypes.object,
};

ChatBoxMessage.defaultProps = {
  messageInfo: {},
  message: {},
};

export default function ChatBoxMessage(props) {
  const { message } = props;
  return (
    <>
      {messageInfo.name && message.text ? (
        <div className="ChatBoxMessage">
          <img
            className="ChatBoxMessageAvatar"
            src={messageInfo.avatarSrc || defaultAvatar}
            alt="avatar user"
          />
          <div className="ChatBoxMessageContent">
            <div className="ChatBoxMessageInfo">
              <h4 className="ChatBoxMessageInfoName">{messageInfo.name}</h4>
              <h5 className="ChatBoxMessageInfoTime">{message.time}</h5>
            </div>
            <div className="ChatBoxMessageContentText">{message.text}</div>
          </div>
        </div>
      ) : (
        <></>
      )}
    </>
  );
}
