import axios from "axios";
import React, { useEffect, useState } from "react";
import ChatBoxMessage from "./ChatBoxMessage";
import ChatBoxMessageOwnerUser from "./ChatBoxMessageOwnerUser";

const URL_MAIN =
  process.env.REACT_APP_URL_MAIN || `https://fanserverapi.herokuapp.com`;

export default function ChatBox({ messageInfo }) {
  const [totalMessages, setTotalMessages] = useState(0);
  const [messages, setMessages] = useState([]);
  const [blocks, setBlocks] = useState(0);
  const [targetId, setTargetId] = useState("");
  const userId = JSON.parse(localStorage.getItem("loginData")).user._id;

  const calBlocks = (totalMess) => {
    return (totalMess - (totalMess % 20)) / 20 + !!(totalMess % 20);
  };

  const getConvsId = async (uId, tId) => {
    let result;
    try {
      result = await axios.post(`${URL_MAIN}/api/conversation/getfriendconv`, {
        userId: uId,
        friendId: tId,
      });
    } catch (err) {
      console.log(err.message);
    }
    return result;
  };

  const getConvsMessages = async (uId, tId, blocksIndex) => {
    let result;
    try {
      result = await axios.post(`${URL_MAIN}/api/message/get`, {
        userId: uId,
        conversationId: tId,
        block: blocksIndex,
      });
    } catch (err) {
      console.log(err.message);
    }
    return result;
  };

  useEffect(() => {
    setTargetId(messageInfo.id);
    getConvsId(userId, targetId).then((res) => {
      console.log(res.data);
    });
  }, [targetId]);

  return (
    <>
      <div className="ChatBox"></div>
    </>
  );
}
