import axios from "axios";
import React, { useState } from "react";

import ChatBox from "./ChatBox";
import ChatBoxFooter from "./ChatBoxFooter";
import ChatBoxHeader from "./ChatBoxHeader";

const URL_MAIN =
  process.env.REACT_APP_URL_MAIN || `https://fanserverapi.herokuapp.com`;

export default function ChatBoxContainer(props) {
  const { currentItem, userId } = props;

  const [currentConvsId, setCurrentConvsId] = useState("");
  const [postMessageData, setPostMessageData] = useState({
    userId: "",
    conversationId: "",
    content: "",
    image: "",
  });

  const onMessageDataChanged = () => {};

  const handlePostMessage = (data) => {
    const conversationId = currentConvsId;
    setPostMessageData({ ...postMessage, userId, conversationId, ...data });

    if (currentItem.contact_type === "group") {
      axios
        .post(`${URL_MAIN}/api/group/message/create`, postMessageData)
        .then((res) => {
          res.data.success
            ? console.log("Gửi tin nhắn thành công", res.data)
            : console.log("gửi tin thất bại", res.data);
        })
        .catch((res) => console.log(res));
    } else {
      console.log(postMessageData);
      axios
        .post(`${URL_MAIN}/api/message/create`, postMessageData)
        .then((res) => {
          res.data.success
            ? console.log("Gửi tin nhắn thành công", res.data)
            : console.log("gửi tin thất bại", res.data);
        })
        .catch((res) => console.log(res));
    }
  };

  const handleConvsIdChange = (convsId) => {
    setCurrentConvsId(convsId);
    console.log(convsId);
  };

  return (
    <>
      <ChatBoxHeader
        headerData={{ avatar: currentItem.avatar, name: currentItem.name }}
      />
      <ChatBox
        chatData={{
          targetId: currentItem.id,
          convs_type: currentItem.contact_type,
          targetAvt: currentItem.avatar,
        }}
        onCurrentConvsIdChange={handleConvsIdChange}
        userId={userId}
      />
      <ChatBoxFooter
        onMessagePost={handlePostMessage}
        conversation={currentConvsId}
      />
    </>
  );
}
