import React, { useEffect, useState } from "react";
import FriendHeader from "./FriendsContactComps/Friend/FriendHeader";
import FriendsContact from "../childComponents/FriendsContactComps/FriendsContact";
import axios from "axios";

const URL_MAIN =
  process.env.REACT_APP_URL_MAIN || "https://fanserverapi.herokuapp.com";

function ContactSideBar(props) {
  const { userId } = props;
  const [friendsData, setFriendsData] = useState([]);
  const [teachersData, setTeachersData] = useState([]);
  const [groupsData, setGroupsData] = useState([]);

  const getFriendsData = async (userId) => {
    let result;
    try {
      result = await axios.post(
        `${URL_MAIN}/api/friend/get`,
        {
          userId: userId,
        },
        {
          header: {
            "Content-Type": "application/json",
          },
        }
      );
    } catch (err) {
      console.log(err);
    }
    return result;
  };

  const getTeachersData = async () => {
    let result;
    try {
      result = await axios.get(
        `${URL_MAIN}/api/user/teachers`,
        {
          userId: userId,
        },
        {
          header: {
            "Content-Type": "application/json",
          },
        }
      );
    } catch (err) {
      console.log(err);
    }
    return result;
  };

  const getGroupsData = async (userId) => {
    let result;
    try {
      result = await axios.post(
        `${URL_MAIN}/api/group/conversation/getconvs`,
        {
          userId: userId,
        },
        {
          header: {
            "Content-Type": "application/json",
          },
        }
      );
    } catch (err) {
      console.log(err);
    }
    return result;
  };

  useEffect(() => {
    Promise.all([
      getFriendsData(userId),
      getTeachersData(userId),
      getGroupsData(userId),
    ])
      .then((result) => {
        const [fData, tData, gData] = result;
        setFriendsData(fData.data);
        setTeachersData(tData.data);
        setGroupsData(gData.data);
        console.log(friendsData, teachersData, groupsData);
      })
      .catch((err) => console.log(err));
  }, []);

  return (
    <div>
      <FriendHeader />
      <FriendsContact
      // listFriends={listFriends}
      // listTeachers={listTeachers}
      // listGroups={listGroups}
      // OnClickContactBtn={OnClickContactBtn}
      />
    </div>
  );
}

export default ContactSideBar;
