import React from "react";
import defaultAvatar from "../../../../../component/Layout/assets/avatar-user.png";
import Avatar from "@mui/material/Avatar";

export default function FriendListItem({ friend, OnClickContactBtn }) {
  const avatarStyles = {
    m: 1,
    border: 2,
    borderColor: "#808080",
  };

  return (
    <>
      <li
        className="FriendListItem"
        onClick={(e) =>
          OnClickContactBtn(e, {
            id: friend._id,
            name: friend.username,
            avatarSrc: friend.picture,
          })
        }
      >
        <Avatar
          src={friend.picture || defaultAvatar}
          alt={friend.username}
          sx={{
            ...avatarStyles,
          }}
        />
        <div className="UserListItemName">{friend.username}</div>
      </li>
    </>
  );
}
