import React from "react";
import defautAvatar from "../../../../component/Layout/assets/avatar-user.png";

export default function ChatBoxHeader({ headerInfo }) {
  return (
    <>
      <div className="ChatBoxHeader">
        <div className="Person">
          <img
            className="FriendAvatar"
            src={headerInfo.avatarSrc || defautAvatar}
            alt="avatar user"
          />
          <span className="FriendName">{headerInfo.name}</span>
        </div>
        <div className="ChatSearch">
          <i className="fas fa-search"></i>
          <input
            type="text"
            className="ChatSearchLabel"
            placeholder="Tìm kiếm tin nhắn"
          />
        </div>
      </div>
    </>
  );
}
