import React from "react";
import defaultAvatar from "../../../../component/Layout/assets/avatar-user.png";

export default function ChatBoxMessage({ messageInfo, message }) {
  const { name, avatarSrc } = messageInfo;
  const { time, text } = message;
  return (
    <>
      <div className="ChatBoxMessage">
        <img
          className="ChatBoxMessageAvatar"
          src={avatarSrc || defaultAvatar}
          alt="avatar user"
        />
        <div className="ChatBoxMessageContent">
          <div className="ChatBoxMessageInfo">
            <h4 className="ChatBoxMessageInfoName">{name}</h4>
            <h5 className="ChatBoxMessageInfoTime">{time}</h5>
          </div>
          <div className="ChatBoxMessageContentText">{text}</div>
        </div>
      </div>
    </>
  );
}
