import axios from "axios";
import React from "react";
import { useState, useEffect } from "react";
import MainLayout from "../../component/MainPage/MainLayout";
import ChatBoxContainer from "./childComponents/ChatBoxComps/ChatBoxContainer";
import ContactSideBar from "./childComponents/ContactSideBar";

import "./Messenger.css";

const TEMP_URL = "http://localhost:3000";
const URL_MAIN =
  process.env.REACT_APP_URL_MAIN || "https://fanserverapi.herokuapp.com";

async function getData(userId) {
  try {
    let friends = axios.post(
      `${URL_MAIN}/api/friend/get`,
      {
        userId: userId,
      },
      {
        headers: {
          "Content-Type": "application/json",
        },
      }
    );
    let teachers = {};
    let groups = axios.post(
      `${URL_MAIN}/api/group/conversation/getconvs`,
      {
        userId: userId,
      },
      {
        headers: {
          "Content-Type": "application/json",
        },
      }
    );
    let result = await Promise.all([friends, groups]);
    console.log(
      result.map((value) => {
        console.log(value);
      })
    );
  } catch (err) {
    console.log(err);
  }
}

export default function Messenger() {
  const [listFriends, setListFriends] = useState([]);
  const [listTeachers, setListTeachers] = useState([]);
  const [listGroups, setListGroups] = useState([]);
  const userId = JSON.stringify(localStorage.getItem("loginData"));
  useEffect(() => {
    try {
      axios.post(URL_MAIN, {});
    } catch (err) {}
  }, []);
  return (
    <>
      <MainLayout />
      <div className="Messenger">
        <div className="MessengerContainer">
          {/* friend list */}

          <ContactSideBar />

          {/* chatbox */}

          <ChatBoxContainer />
        </div>
      </div>
    </>
  );
}
