import axios from "axios";
import React, { useEffect, useState } from "react";
import ChatBoxMain from "./ChatBoxMain";
import ChatBoxMessage from "./ChatBoxMessage";
import ChatBoxMessageOwnerUser from "./ChatBoxMessageOwnerUser";

const URL_MAIN =
  process.env.REACT_APP_URL_MAIN || `https://fanserverapi.herokuapp.com`;

const block = 1; //testing

export default function ChatBox(props) {
  const { chatData, userId } = props;
  const [convsId, setConvsId] = useState("");
  const [messagesData, setMessagesData] = useState([]);

  const getFriendConvsId = (userId, targetId) => {
    return axios.post(`${URL_MAIN}/api/conversation/getfriendconv`, {
      userId: userId,
      friendId: targetId,
    });
  };

  const getFriendMessages = (userId, convsId, block) => {
    return axios.post(`${URL_MAIN}/api/message/getblock`, {
      userId: userId,
      conversationId: convsId,
      block: block,
    });
  };

  useEffect(() => {
    if (chatData.contact_type === "friend") {
      getFriendConvsId(userId, chatData.targetId)
        .then((res) => {
          const convsId = res.data.conversation;
          return getFriendMessages(userId, convsId, block);
        })
        .then((res) => {
          const result = res.data.messages;
          setMessagesData(result);
        })
        .catch((err) => console.log(err));
    }
  }, [userId, chatData]);

  return (
    <>
      <div className="ChatBox">
        <ChatBoxMain data={messagesData} />
      </div>
    </>
  );
}
