import axios from "axios";
import React, { useEffect, useState } from "react";
import GroupListItem from "./GroupListItem";

const TEMP_URL = "http://localhost:3000";
const URL_MAIN =
  process.env.REACT_APP_URL_MAIN || "https://fanserverapi.herokuapp.com";
export default function GroupList({ listGroups }) {
  const [GroupsData, setGroupsData] = useState([]);
  useEffect(() => {
    listGroups.map(async (groupId) => {
      try {
        let result = await axios.post(
          `${TEMP_URL}/api/group/conversation/get`,
          {
            conversationId: groupId,
          },
          {
            header: {
              "Content-Type": "application/json",
            },
          }
        );
        setGroupsData((prevState) => [...prevState, result.data.conversation]);
      } catch (err) {
        console.log(err);
      }
    });
  });
  const showAllItems =
    GroupsData.length > 0 ? (
      GroupsData.map((group) => <GroupListItem group={group} />)
    ) : (
      <GroupListItem group={[]} />
    );

  return (
    <>
      <ul className="GroupList">
        <i className="fab fa-battle-net"></i>
        Group
        <input
          type="checkbox"
          hidden
          id="OpenGroupList"
          className="ShowGroupListBtn"
        />
        <label htmlFor="OpenGroupList">
          <i className="ShowListIcon fas fa-angle-down"></i>
        </label>
        {showAllItems}
      </ul>
    </>
  );
}
