import axios from "axios";
import React, { useEffect } from "react";
import ChatBoxMessage from "./ChatBoxMessage";

const URL_MAIN =
  process.env.REACT_APP_URL_MAIN || `https://fanserverapi.herokuapp.com`;

export default function ChatBox({ messageInfo }) {
  const getConvsId = async (uId, tId) => {};

  useEffect(() => {
    const userId = JSON.parse(localStorage.getItem("loginData")).user._id;
    const targetId = messageInfo.id;
    try {
      axios.post(`${URL_MAIN}/api/conversation/getfriendconv`, {
        userId: userId,
        friendId: targetId,
      });
    } catch (err) {
      console.log(err);
    }
  }, []);

  return (
    <>
      <div className="ChatBox">
        <div className="ChatBoxMain">
          <div className="ChatBoxMessageOwnerUser">
            <img
              className="ChatBoxMessageAvatar"
              src="https://cdn3.vectorstock.com/i/1000x1000/64/42/passed-stamp-checkmark-sign-and-symbol-vector-14856442.jpg"
              alt="avatar user"
            />
          </div>
          <ChatBoxMessage />
        </div>
      </div>
    </>
  );
}
