import React, { useEffect, useRef } from "react";

import ChatBox from "./ChatBox";
import ChatBoxFooter from "./ChatBoxFooter";
import ChatBoxHeader from "./ChatBoxHeader";

export default function ChatBoxContainer({ contactInfo }) {
  const value = useRef({});
  useEffect(() => {
    value.current = contactInfo;
    console.log(value.current);
  }, []);
  return (
    <>
      <ChatBoxHeader headerInfo={contactInfo} />
      <ChatBox messageInfo={contactInfo} />
      <ChatBoxFooter />
    </>
  );
}
