import axios from "axios";
import React from "react";
import { useState, useEffect } from "react";
import MainLayout from "../../component/MainPage/MainLayout";
import ChatBoxContainer from "./childComponents/ChatBoxComps/ChatBoxContainer";
import ContactSideBar from "./childComponents/ContactSideBar";

import "./Messenger.css";

const TEMP_URL = "http://localhost:3000";
const URL_MAIN =
  process.env.REACT_APP_URL_MAIN || "https://fanserverapi.herokuapp.com";

export default function Messenger() {
  const [listFriends, setListFriends] = useState([]);
  const [listTeachers, setListTeachers] = useState([]);
  const [listGroups, setListGroups] = useState([]);
  const [groupsData, setGroupsData] = useState([]);
  const userId = JSON.parse(localStorage.getItem("loginData")).user._id;

  const getListGConvs = async (listGroups) => {
    let results = [];
    let promises = [];
    await Promise.all(
      listGroups.map((groupId) => {
        axios
          .post(`${URL_MAIN}/api/group/conversation/get`, {
            conversationId: groupId,
          })
          .then((res) => {
            results.push(res);
          });
      })
    );
    return results;
  };

  const getListFriends = async (userId) => {
    let result;
    try {
      result = await axios.post(`${URL_MAIN}/api/friend/get`, {
        userId: userId,
      });
    } catch (err) {
      console.log(err.message);
    }
    return result;
  };

  const getListTeachers = async (userId) => {
    let result;
    try {
      result = await axios.get(`${URL_MAIN}/api/user/teachers`);
    } catch (err) {
      console.log(err.message);
    }
    return result.data.teachers;
  };

  const getListGroups = async (userId) => {
    let result;
    try {
      result = await axios.post(`${URL_MAIN}/api/group/conversation/getconvs`, {
        userId: userId,
      });
    } catch (err) {
      console.log(err.message);
    }
    return result;
  };

  useEffect(() => {
    getListFriends(userId)
      .then((res) => {
        setListFriends(res.data.friends);
        console.log(
          "ðŸš€ ~ file: Messenger.jsx ~ line 75 ~ .then ~ res.data.friends",
          res.data.friends
        );
      })
      .catch((err) => console.log(err));
    getListTeachers(userId)
      .then((res) => {
        setListTeachers(res);
      })
      .catch((err) => console.log(err));
    getListGroups(userId)
      .then((res) => {
        setListGroups(res.data.GroupConversations);
      })
      .catch((err) => console.log(err));
    getListGConvs(listGroups);
    // let LGs = listGroups.map(async (groupId) => {
    //   let result;
    //   console.log("reun");
    //   try {
    //     result = await axios.post(`${URL_MAIN}/api/group/conversation/get`, {
    //       conversationId: groupId,
    //     });
    //   } catch (err) {
    //     console.log(err);
    //   }
    //   return result;
    // });

    // Promise.all(LGs).then((values) => {
    //   setGroupsData(values);
    //   console.log(
    //     "ðŸš€ ~ file: Messenger.jsx ~ line 112 ~ Promise.all ~ values",
    //     values
    //   );
    // });
  }, []);

  return (
    <>
      <MainLayout />
      <div className="Messenger">
        <div className="MessengerContainer">
          {/* friend list */}

          <ContactSideBar
            listFriends={listFriends}
            listTeachers={listTeachers}
            groupsData={groupsData}
          />

          {/* chatbox */}

          <ChatBoxContainer />
        </div>
      </div>
    </>
  );
}
