import React, { useState, useRef, useEffect } from "react";
import axios from "axios";
import FriendItem from "./FriendItem";

const URL_MAIN =
  process.env.REACT_APP_URL_MAIN || "https://fanserverapi.herokuapp.com";

export default function FriendList() {
  const [listFriends, setListFriends] = useState([]);
  const ref = useRef(null);

  const getFrContact = async (e) => {
    const userId = JSON.parse(localStorage.getItem("loginData")).user._id;

    // get friend list
    let result;
    try {
      result = await axios.post(
        `${URL_MAIN}/api/friend/get`,
        {
          userId: userId,
        },
        {
          header: {
            "Content-Type": "application/json",
          },
        }
      );
    } catch (err) {
      console.log(err);
    }
    return result;
  };

  const handleOnClick = () => {
    getFrContact().then((res) => {
      setListFriends(res.data);
      console.log(
        "ðŸš€ ~ file: FriendList.jsx ~ line 38 ~ getFrContact ~ res.data",
        res.data
      );
    });
  };

  const showAllItems =
    listFriends.length > 0 ? (
      listFriends.map((friend) => (
        <FriendListItem key={friend._id} friend={friend} />
      ))
    ) : (
      <></>
    );
  return (
    <>
      <ul className="FriendList" ref={ref}>
        <i className="fab fa-battle-net"></i>
        Friend
        <input
          type="checkbox"
          hidden
          id="OpenFriendList"
          className="ShowFriendListBtn"
          onClick={handleOnClick}
        />
        <label htmlFor="OpenFriendList">
          <i className="ShowListIcon fas fa-angle-down"></i>
        </label>
        {showAllItems}
      </ul>
    </>
  );
}
