import React from "react";
import defautAvatar from "../../../../component/Layout/assets/avatar-user.png";
import Avatar from "@mui/material/Avatar";
import PropTypes from "prop-types";

ChatBoxHeader.prototype = {
  headerInfo: PropTypes.object,
};

ChatBoxHeader.defaultProps = {
  headerInfo: {},
};

export default function ChatBoxHeader({ headerInfo }) {
  const avatarStyles = {
    ml: 1,
    mr: 1,
    border: 2,
    borderColor: "#808080",
  };
  console.log(headerInfo);
  return (
    <>
      <div className="ChatBoxHeader">
        <div className="Person">
          {/* <img
            className="FriendAvatar"
            src={headerInfo.avatarSrc || defautAvatar}
            alt="avatar user"
          /> */}
          <Avatar
            src={headerInfo.avatarSrc || defautAvatar}
            sx={{ ...avatarStyles }}
          />
          <span className="FriendName">{headerInfo.name}</span>
        </div>
        <div className="ChatSearch">
          <i className="fas fa-search"></i>
          <input
            type="text"
            className="ChatSearchLabel"
            placeholder="Tìm kiếm tin nhắn"
          />
        </div>
      </div>
    </>
  );
}
